function TypingEffect({text, delay, color}) {
    const colors = {
      reset: '\x1b[0m',
      black: '\x1b[30m',
      red: '\x1b[31m',
      green: '\x1b[32m',
      yellow: '\x1b[33m',
      blue: '\x1b[34m',
      magenta: '\x1b[35m',
      cyan: '\x1b[36m',
      white: '\x1b[37m'
    };
    
    let index = 0;
    const colorCode = colors[color] || colors.reset;
    
    setInterval(() => {
      process.stdout.write(colorCode + text.charAt(index) + colors.reset);
      index++;
      if (index === text.length) {
        console.log();
        clearInterval();
      }
    }, delay);
  }
    module.exports = TypingEffect;
